import axios from "axios";
import React, { useEffect, useState } from "react";
import "./categories.css";

export default function Categories() {
  const [menu, setMenu] = useState({ data: [] }); // Initialize state with an object that has a data property

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:4545/");
        setMenu(response.data); // Assuming the data is in the response.data
      } catch (e) {
        console.log(e);
      }
    };

    fetchData(); // Invoke the function to fetch data
  }, []);

  return (
    <>
      <div className="ContainerCategories">
        <h2>What would you like to buy? </h2>
        <div className="categories">
          {/* Conditional rendering to ensure menu.data exists */}
          {menu.data && menu.data.map((item) => (
            <div key={item.id} className="categoriesItem"> {/* Make sure each item has a unique key */}
              {item.category}
            </div>
          ))}
        </div>
      </div>
    </>
  );
}
