import React, { useEffect, useState } from "react";
import axios from "axios";
import "./categories.css";
import { useNavigate } from "react-router-dom";

export default function Categories() {
  const [menu, setMenu] = useState({ data: [] });
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:4545/`)
      .then((response) => setMenu({ data: response.data }))
      .catch((e) => console.log(e));
  }, []);

  const getToSub = (id) => {
    // Navigate to the subcategories route with the category id as a parameter
    navigate(`/subcategories/${id}`); // Adjust the path as needed
  }

  return (
    <>
      <div className="ContainerCategories">
        <h2>What would you like to buy? </h2>
        <div className="categories">
          {menu.data.map((e) => (
            <div key={e._id} onClick={() => getToSub(e._id)}>
              <img className="imgCategory" src={e.image} alt={e.category} />
              <div className="categoryName">{e.category}</div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}
