import React, { useState, createContext } from "react";
import Categories from "../categories/Categories";
import ItemInfo from "../itemInfo/ItemInfo";
import Items from "../items/Items";
import SubCategories from "../subCategories/SubCategories";
import "./content.css";
import { Route, Routes } from "react-router-dom";

// Define the context with an initial value
export const ItemsContext = createContext();

export default function Content() {
  const [currentItems, setCurrentItems] = useState([]);

  return (
    <div className="content">
      {/* Provide the context with currentItems and setCurrentItems */}
      <ItemsContext.Provider value={{ currentItems, setCurrentItems }}>
        <Routes>
          <Route path="/subcategories/:catName/:id" element={<SubCategories />} />
          <Route path="/" element={<Categories />} />
        </Routes>
        <Items />
        <ItemInfo />
      </ItemsContext.Provider>
    </div>
  );
}
